import React, { useState, useEffect } from 'react';
import './Wishlist.css';

const Wishlist = () => {
  const generateRandomPrice = () => {
    return `₹${(Math.random() * (100 - 10) + 10).toFixed(2)}`; // Generates a random price between ₹10 and ₹100
  };

  const allAvailableItems = [
    { id: 1, name: 'Apples', price: generateRandomPrice(), picture: 'https://ychef.files.bbci.co.uk/1280x720/p07v2wjn.jpg' },
    { id: 2, name: 'Oranges', price: generateRandomPrice(), picture: 'data:image/jpeg;base64,' },
    { id: 3, name: 'Cucumbers', price: generateRandomPrice(), picture: 'data:image/jpeg;base64,' },
    { id: 4, name: 'Bananas', price: generateRandomPrice(), picture: 'https://blog-images-1.pharmeasy.in/blog/production/wp-content/uploads/2021/01/30152155/shutterstock_518328943-1.jpg' },
    { id: 5, name: 'Grapes', price: generateRandomPrice(), picture: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSTlu8lEuJsndjsqCCBxoi7xStrOBeIhCsRlg&usqp=CAU' },
    { id: 6, name: 'Tomatoes', price: generateRandomPrice(), picture: 'https://media.post.rvohealth.io/wp-content/uploads/2020/09/AN313-Tomatoes-732x549-Thumb.jpg' },
    { id: 7, name: 'Capsicum', price: generateRandomPrice(), picture: 'https://fastandfresh.in/cdn/shop/products/greencapcicum_800x.jpg?v=1655522147' },
    { id: 8, name: 'Carrot', price: generateRandomPrice(), picture: 'https://www.shutterstock.com/image-photo/pile-carrots-closeup-young-vegetables-260nw-1739946131.jpg' }
  ];

  const [items, setItems] = useState(
    JSON.parse(localStorage.getItem('wishlistItems')) || []
  );
  const [cart, setCart] = useState(
    JSON.parse(localStorage.getItem('cartItems')) || []
  );
  const [searchTerm, setSearchTerm] = useState('');
  const [notification, setNotification] = useState('');

  useEffect(() => {
    localStorage.setItem('wishlistItems', JSON.stringify(items));
  }, [items]);

  useEffect(() => {
    localStorage.setItem('cartItems', JSON.stringify(cart));
  }, [cart]);

  const removeItem = (id) => {
    setItems(items.filter(item => item.id !== id));
    showNotification('Item removed from wishlist');
  };

  const addToWishlist = (item) => {
    if (!items.some(existingItem => existingItem.id === item.id)) {
      setItems([...items, item]);
      showNotification('Item added to wishlist');
    } else {
      showNotification('Item already in wishlist');
    }
  };

  const addToCart = (item) => {
    setCart([...cart, item]);
    showNotification('Item added to cart');
  };

  const removeFromCart = (index) => {
    setCart(cart.filter((_, i) => i !== index));
    showNotification('Item removed from cart');
  };

  const clearWishlist = () => {
    setItems([]);
    showNotification('Wishlist cleared');
  };

  const clearCart = () => {
    setCart([]);
    showNotification('Cart cleared');
  };

  const handleQuantityChange = (id, quantity) => {
    const updatedItems = items.map(item =>
      item.id === id ? { ...item, quantity: parseInt(quantity, 10) } : item
    );
    setItems(updatedItems);
  };

  const filteredItems = allAvailableItems.filter(item =>
    item.name.toLowerCase().includes(searchTerm.toLowerCase())
  );

  const showNotification = (message) => {
    setNotification(message);
    setTimeout(() => {
      setNotification('');
    }, 2000);
  };

  return (
    <div className="wishlist">
      <h2>Wishlist</h2>
      {notification && <div className="notification">{notification}</div>}
      <input
        type="text"
        placeholder="Search items..."
        value={searchTerm}
        onChange={(e) => setSearchTerm(e.target.value)}
        className="search-input"
      />
      <button onClick={clearWishlist} className="clear-button">Clear Wishlist</button>
      <ul>
        {items.map(item => (
          <li key={item.id}>
            <div className="item">
              <img src={item.picture} alt={item.name} className="item-picture" />
              <div className="item-details">
                <span>{item.name}</span>
                <span>{item.price}</span>
                <input
                  type="number"
                  value={item.quantity}
                  min="1"
                  onChange={(e) => handleQuantityChange(item.id, e.target.value)}
                  className="quantity-input"
                />
              </div>
              <div>
                <button onClick={() => removeItem(item.id)}>Remove</button>
                <button onClick={() => addToCart(item)}>Add to Cart</button>
              </div>
            </div>
          </li>
        ))}
      </ul>
      <div className="available-items">
        <h3>Available Items</h3>
        <ul>
          {filteredItems.filter(item => !items.some(wishlistItem => wishlistItem.id === item.id)).map(item => (
            <li key={item.id}>
              <div className="item">
                <img src={item.picture} alt={item.name} className="item-picture" />
                <div className="item-details">
                  <span>{item.name}</span>
                  <span>{item.price}</span>
                </div>
                <button onClick={() => addToWishlist(item)}>Add to Wishlist</button>
              </div>
            </li>
          ))}
        </ul>
      </div>
      <div className="cart">
        <h3>Cart Items ({cart.length})</h3>
        {cart.length > 0 && <button onClick={clearCart} className="clear-button">Clear Cart</button>}
        <ul>
          {cart.map((item, index) => (
            <li key={index}>
              <img src={item.picture} alt={item.name} className="cart-picture" />
              {item.name} - {item.price} (x{item.quantity})
              <button onClick={() => removeFromCart(index)}>Remove</button>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default Wishlist;
